<?phpclass Model_EmployeeCustom extends \Orm\Model {    protected static $_table_name = "employees_customs";    protected static $_properties = array(        'id',        'employee_id',        'layout',        'theme',        'display_name_font',        'display_name_font_size',        'display_name_font_color',        'display_position_font',        'display_position_font_color',        'social_link_badges_color',        'wallpaper',        'working_status',        'resume_published',        'portfolio_published',        'created_at',        'updated_at'    );    protected static $_observers = array(        'Orm\Observer_CreatedAt' => array(            'events' => array('before_insert'),            'mysql_timestamp' => false,        ),        'Orm\Observer_UpdatedAt' => array(            'events' => array('before_save'),            'mysql_timestamp' => false,        ),    );    public static function validate($factory) {        $val = Validation::forge($factory);        return $val;    }    public static function get_employee_custom($employee_id){        $custom = DB::select('*')->from('employees_customs')            ->where('employee_id',"=",$employee_id)            ->execute()->as_array();        if(count($custom)) return Model_EmployeeCustom::find($custom[0]['id']);        else return null;    }}