<?phpclass Model_Experience extends \Orm\Model {    protected static $_table_name = "experiences";    protected static $_properties = array(        'id',        'employee_id',        'exp_company',        'exp_position',        'exp_start_year',        'exp_end_year',        'exp_order',        'created_at',        'updated_at'    );    protected static $_observers = array(        'Orm\Observer_CreatedAt' => array(            'events' => array('before_insert'),            'mysql_timestamp' => false,        ),        'Orm\Observer_UpdatedAt' => array(            'events' => array('before_save'),            'mysql_timestamp' => false,        ),    );    public static function validate($factory) {        $val = Validation::forge($factory);        return $val;    }    public static function get_experience($employee_id) {        $query = DB::select('*')->from('experiences')            ->where('employee_id','=',$employee_id)            ->order_by('exp_order','asc')            ->execute()->as_array();        return $query;    }    public static function get_experience_ids($employee_id) {        $query = DB::select('id')->from('experiences')            ->where('employee_id','=',$employee_id)            ->execute()->as_array();        $result = array();        foreach($query as $q){            $result[] = $q['id'];        }        return $result;    }}