<?phpclass Model_Portfolio extends \Orm\Model {    protected static $_table_name = "portfolios";    protected static $_properties = array(        'id',        'employee_id',        'port_title',        'port_photo',        'created_at',        'updated_at'    );    protected static $_observers = array(        'Orm\Observer_CreatedAt' => array(            'events' => array('before_insert'),            'mysql_timestamp' => false,        ),        'Orm\Observer_UpdatedAt' => array(            'events' => array('before_save'),            'mysql_timestamp' => false,        ),    );    public static function validate($factory) {        $val = Validation::forge($factory);        return $val;    }    public static function get_portfolios($employee_id) {        $query = DB::select('*')->from('portfolios')            ->where('employee_id','=',$employee_id)            ->execute()->as_array();        return $query;    }    public static function get_portfolio_ids($employee_id) {        $query = DB::select('id')->from('portfolios')            ->where('employee_id','=',$employee_id)            ->execute()->as_array();        $result = array();        foreach($query as $q){            $result[] = $q['id'];        }        return $result;    }}