<?phpclass Model_Skill extends \Orm\Model {    protected static $_table_name = "skills";    protected static $_properties = array(        'id',        'employee_id',        'skill_type',        'skill_title',        'skill_level',        'skill_order',        'created_at',        'updated_at'    );    protected static $_observers = array(        'Orm\Observer_CreatedAt' => array(            'events' => array('before_insert'),            'mysql_timestamp' => false,        ),        'Orm\Observer_UpdatedAt' => array(            'events' => array('before_save'),            'mysql_timestamp' => false,        ),    );    public static function validate($factory) {        $val = Validation::forge($factory);        return $val;    }    public static function get_language_skills($employee_id) {        $query = DB::select('*')->from('skills')            ->where('employee_id','=',$employee_id)            ->and_where('skill_type','=','language')            ->order_by('skill_order','asc')            ->execute()->as_array();        return $query;    }    public static function get_language_skill_ids($employee_id) {        $query = DB::select('id')->from('skills')            ->where('employee_id','=',$employee_id)            ->and_where('skill_type','=','language')            ->execute()->as_array();        $result = array();        foreach($query as $q){            $result[] = $q['id'];        }        return $result;    }    public static function get_computer_skills($employee_id) {        $query = DB::select('*')->from('skills')            ->where('employee_id','=',$employee_id)            ->and_where('skill_type','=','computer')            ->order_by('skill_order','asc')            ->execute()->as_array();        return $query;    }    public static function get_computer_skill_ids($employee_id) {        $query = DB::select('id')->from('skills')            ->where('employee_id','=',$employee_id)            ->and_where('skill_type','=','computer')            ->execute()->as_array();        $result = array();        foreach($query as $q){            $result[] = $q['id'];        }        return $result;    }    public static function get_other_skills($employee_id) {        $query = DB::select('*')->from('skills')            ->where('employee_id','=',$employee_id)            ->and_where('skill_type','=','other')            ->order_by('skill_order','asc')            ->execute()->as_array();        return $query;    }    public static function get_other_skill_ids($employee_id) {        $query = DB::select('id')->from('skills')            ->where('employee_id','=',$employee_id)            ->and_where('skill_type','=','other')            ->execute()->as_array();        $result = array();        foreach($query as $q){            $result[] = $q['id'];        }        return $result;    }}