<section class="widget">    <legend class="section">ข้อมูลการชำระเงิน</legend>    <div class="body">        <table class="table">            <tr>                <td>Ref. No.</td>                <td><?php echo $payment->ref_no; ?></td>            </tr>            <tr>                <td>Username</td>                <td><?php echo $user->username; ?></td>            </tr>            <tr>                <td>ชื่อผู้ว่าจ้าง</td>                <td><?php echo $employer['employer_name']; ?></td>            </tr>            <tr>                <td>จำนวนบัฟที่ซื้อ</td>                <td><?php echo number_format($payment->buff_amount,0); ?></td>            </tr>            <tr>                <td>ราคา</td>                <td><?php echo number_format($payment->price,2); ?> บาท</td>            </tr>            <tr>                <td>ช่องทางการชำระเงิน</td>                <td><?php                    if($payment->payment_method == "bank_transfer") echo "โอนเงินผ่านธนาคาร";                ?></td>            </tr>            <tr>                <td>วันที่ซื้อ</td>                <td><?php echo date('M d, Y H:i',$payment->created_at); ?></td>            </tr>            <tr>                <td>อัพเดตสถานะเมื่อ</td>                <td><?php echo date('M d, Y H:i',$payment->updated_at); ?></td>            </tr>            <tr>                <td>สถานะ</td>                <td><?php                    switch($payment->status){                        case 0:                            echo '<i class="fa fa-minus-circle"></i> ยกเลิก';                            break;                        case 1:                            echo '<i class="fa fa-circle-o"></i> รอหลักฐานการชำระเงิน';                            break;                        case 2:                            echo '<i class="fa fa-circle-o"></i> รอการตรวจสอบการชำระเงิน';                            break;                        case 3:                            echo '<i class="fa fa-check-circle"></i> เสร็จสิ้น';                            break;                    }                ?></td>            </tr>            <?php if($payment->status == 1) { ?>            <tr>                <td>วันสุดท้ายของการชำระเงิน</td>                <td><?php echo date('M d, Y H:i',$payment->expired_at); ?></td>            </tr>            <?php } ?>        </table>    </div></section><section class="widget">    <legend class="section">หลักฐานการชำระเงิน</legend>    <div class="body">        <?php if($confirm){ ?>        <table class="table">            <tr>                <td style="width:40%">โอนเข้าบัญชี</td>                <td style="width:60%"><?php                    if($confirm['transfer_to_account'] == 1) echo "ธ.กสิกรไทย";                ?></td>            </tr>            <tr>                <td>จำนวนที่โอน</td>                <td><?php echo number_format($confirm['transfer_amount'],2); ?></td>            </tr>            <tr>                <td>วัน - เวลาที่โอน</td>                <td><?php echo $confirm['transfer_datetime']; ?></td>            </tr>            <tr>                <td>ไฟล์ภาพหลักฐาน</td>                <td>                    <img src="<?php echo strlen($confirm['attachment'])?("http://www.buffohero.com/uploads/payment_evidence/".$confirm['attachment']):"http://placehold.it/640x480/eee.png"; ?>" style="width:100%;" />                </td>            </tr>            <tr>                <td>หมายเหตุ</td>                <td><?php echo $confirm['remark']; ?></td>            </tr>        </table>        <?php } else { ?>            <p>ยังไม่มีหลักฐานการชำระเงิน หรือได้รับการยกเว้น</p>        <?php } ?>    </div></section><section class="widget">    <a class="btn btn-default" href="<?php echo Uri::create('payment'); ?>">ย้อนกลับ</a>    <div class="pull-right">        <?php if($payment->status == 1){ ?>            <a class="btn btn-danger" href="javascript:void(0);" onclick="beforeCancel(<?php echo $payment->id; ?>);">ยกเลิกรายการชำระเงิน</a>        <?php } ?>        <?php if($payment->status == 1 || $payment->status == 2){ ?>            <a class="btn btn-success" href="javascript:void(0);" onclick="beforeConfirm(<?php echo $payment->id; ?>);">อนุมัติรายการชำระเงิน</a>        <?php } ?>    </div></section><script type="text/javascript">    function beforeCancel(id){        var conf = confirm("ต้องการยกเลิกรายการชำระเงินนี้?");        if(!conf) return false;        window.location.href = "<?php echo Uri::create('payment/cancel/'); ?>"+id;    }    function beforeConfirm(id){        var conf = confirm("ต้องการยืนยันรายการชำระเงินนี้?");        if(!conf) return false;        window.location.href = "<?php echo Uri::create('payment/confirm/'); ?>"+id;    }</script>